import React, { useState } from 'react';
import axios from 'axios';
import Rating from './Rating';

function ServiceList() {
  const [area, setArea] = useState('');
  const [results, setResults] = useState([]);

  const search = async () => {
    const res = await axios.get(`http://localhost:5000/api/services/search?area=${area}`);
    setResults(res.data);
  };

  return (
    <div>
      <h2>Search Service Providers</h2>
      <input placeholder="Enter Area" value={area} onChange={(e) => setArea(e.target.value)} />
      <button onClick={search}>Search</button>

      {results.length > 0 && results.map((s) => (
        <div key={s.id} style={{ border: '1px solid #ccc', padding: 10, marginTop: 10 }}>
          <p><strong>{s.name}</strong> ({s.service})</p>
          <p>Area: {s.area}</p>
          <p>Phone: {s.phone}</p>
          <Rating id={s.id} rating={s.rating} />
        </div>
      ))}
    </div>
  );
}

export default ServiceList;
