import React, { useState } from 'react';
import axios from 'axios';
import Rating from './Rating';

function ServiceList() {
  const [area, setArea] = useState('');
  const [results, setResults] = useState([]);

  const search = async () => {
    const res = await axios.get(`http://localhost:5000/api/services/search?area=${area}`);
    setResults(res.data);
  };

  return (
    <div>
      <div className="search-box">
        <h2>üîç Search Service Providers</h2>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Enter area (e.g., Ameerpet)"
            value={area}
            onChange={(e) => setArea(e.target.value)}
          />
          <button onClick={search}>Search</button>
        </div>
      </div>

      {results.length === 0 && (
        <p style={{ textAlign: 'center', color: '#999' }}>No services found.</p>
      )}

      {results.length > 0 &&
        results.map((s) => (
          <div key={s.id} className="service-card">
            <h3>{s.name}</h3>
            <p><strong>Service:</strong> {s.service}</p>
            <p><strong>Area:</strong> {s.area}</p>
            <p><strong>Phone:</strong> {s.phone}</p>
            <Rating id={s.id} rating={s.rating} />
          </div>
        ))}
    </div>
  );
}

export default ServiceList;
