import React, { useState } from 'react';
import ServiceForm from './components/ServiceForm';
import ServiceList from './components/ServiceList';
import AllServices from './components/AllServices';

function App() {
  const [view, setView] = useState("home");

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-md p-6">
        <h1 className="text-2xl font-bold text-center mb-6 text-blue-600">
          ðŸ“ž Hyderabad Services Phonebook
        </h1>

        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setView("home")} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
            Home
          </button>
          <button onClick={() => setView("all")} className="bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-800">
            View All
          </button>
        </div>

        {view === "home" && (
          <>
            <ServiceForm />
            <ServiceList />
          </>
        )}
        {view === "all" && <AllServices />}
      </div>
    </div>
  );
}

export default App;
